mutation DeletePost($input: DeletePostInput!) {
  deletePost(input: $input) {
    blog {
      author {
        createdAt
        id
        updatedAt
      }
      blogAuthorId
      blogId
      createdAt
      name
      posts {
        items {
          createdAt
          postId
          rating
          title
          updatedAt
        }
        nextToken
      }
      siteId
      updatedAt
    }
    comments {
      items {
        commentId
        content
        createdAt
        post {
          createdAt
          postId
          rating
          title
          updatedAt
        }
        updatedAt
      }
      nextToken
    }
    createdAt
    postId
    rating
    title
    updatedAt
  }
}
