query GetPost($id: ID!) {
  getPost(id: $id) {
    blog {
      createdAt
      id
      name
      updatedAt
    }
    comments {
      items {
        createdAt
        id
        post {
          id
        }
        text
        updatedAt
      }
    }
    createdAt
    id
    name
    updatedAt
  }
}
